mapper.insert:
INSERT INTO HALL(
	HALL_CODE
	, HALL_NAME
	, HALL_SEAT_CNT
	, HALL_RENT_PRICE
	, HALL_STATUS
	, HALL_DETAIL
	, USER_ID
) VALUES(
	#{hallCode}
	, #{hallName}
	, #{hallSeatCnt}
	, #{hallRentPrice}
	, #{hallStatus}
	, #{hallDetail}
	, #{userId}
)

INSERT INTO HALL_DATE(
	HALL_DATE_CODE
	, HALL_RENT_DATE
	, HALL_CODE
)

	SELECT
		(SELECT 'H_DATE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(HALL_DATE_CODE, 8))), 0) + 1 + #{i}, 6, 0) FROM HALL_DATE)
		, #{hallDate.hallRentDate}
		, #{hallCode}
	FROM DUAL

INSERT INTO HALL_IMG(
	HALL_IMG_CODE
	, HALL_IMG_NAME_ORIGIN
	, HALL_IMG_NAME_ATTACHED
	, HALL_IMG_IS_MAIN
	, HALL_CODE
)

	SELECT
		(SELECT 'H_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(HALL_IMG_CODE, 7))), 0) + 1 + #{i}, 6, 0) FROM HALL_IMG)
		, #{hallImg.hallImgNameOrigin}
		, #{hallImg.hallImgNameAttached}
		, #{hallImg.hallImgIsMain}
		, #{hallCode}
	FROM DUAL

INSERT INTO(
	HALL_CODE
	, HALL_SEAT_R_CNT
	, HALL_SEAT_S_CNT
	, HALL_SEAT_A_CNT
) VALUES(
	#{hallCode}
	, #{hallSeat.hallSeatRCnt}
	, #{hallSeat.hallSeatSCnt}
	, #{hallSeat.hallSeatACnt}
)
