mapper.insert:
INSERT INTO QBOARD
	(QBOARD_NUM
	, QBOARD_TITLE
	, QBOARD_CONTENT
	, USER_ID
	)
VALUES (
	QBOARD_NUM_SEQ.NEXTVAL
	, #{qboardTitle}
	, #{qboardContent}
	, #{userId}
	)

mapper.select:
SELECT ROW_NUM , QBOARD_NUM , QBOARD_TITLE , USER_ID , QBOARD_CREATE_DATE FROM ( SELECT ROWNUM AS ROW_NUM , QBOARD_NUM , QBOARD_TITLE , USER_ID , QBOARD_CREATE_DATE FROM ( SELECT QBOARD_NUM , QBOARD_TITLE , USER_ID , QBOARD_CREATE_DATE FROM SPRING_BOARD
WHERE UPPER(${searchKeyword}) LIKE UPPER('%'||#{searchValue}||'%')
ORDER BY QBOARD_NUM DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}
